CREATE TABLE BOOKS (ISBN VARCHAR(10) not null primary key, TITLE VARCHAR(50) not null, PUBLISHED_DATE DATE, PUBLISHER VARCHAR(50), DESCRIPTION VARCHAR(500))
CREATE TABLE AUTHOR (ID INTEGER not null primary key, FIRST_NAME VARCHAR(20) not null, LAST_NAME VARCHAR(20), BIO VARCHAR(1000), EMAIL VARCHAR(30))
CREATE TABLE BOOKS_AUTHOR (BOOKS_ISBN VARCHAR(10), AUTHOR_ID INTEGER, primary key (BOOKS_ISBN, AUTHOR_ID))
ALTER TABLE BOOKS_AUTHOR ADD CONSTRAINT BOOKSISBN_FK FOREIGN KEY (BOOKS_ISBN) REFERENCES BOOKS (ISBN)
ALTER TABLE BOOKS_AUTHOR ADD CONSTRAINT AUTHOR_FK FOREIGN KEY (AUTHOR_ID) REFERENCES AUTHOR (ID)